// Grid.js
import React from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols }) => {
  const grid = Array.from({ length: rows }, () =>
    Array.from({ length: cols }, () => Math.random() > 0.5)
  );

  return (
    <div className="grid">
      {grid.map((row, i) =>
        <div key={i} className="grid-row">
          {row.map((cell, j) =>
            <Cell key={`${i}-${j}`} alive={cell} />
          )}
        </div>
      )}
    </div>
  );
};

export default Grid;
