// LifeSimulationContext.js
import React, { createContext, useContext, useState } from 'react';

const LifeSimulationContext = createContext();

export const useLifeSimulation = () => {
  return useContext(LifeSimulationContext);
};

export const LifeSimulationProvider = ({ children }) => {
  const [rows, setRows] = useState(20); // 改动
  const [cols, setCols] = useState(20); // 改动
  const [cellStates, setCellStates] = useState([]); // 改动
  const [livingCells, setLivingCells] = useState(0); // 改动
  const [cntCellDeadCum, setCellDeadCum] = useState([]); // 改动

  return (
    <LifeSimulationContext.Provider
      value={{
        rows,
        setRows,
        cols,
        setCols,
        cellStates,
        setCellStates,
        livingCells,
        setLivingCells,
        cntCellDeadCum,
        setCellDeadCum
      }}
    >
      {children}
    </LifeSimulationContext.Provider>
  );
};
