import React, { useState } from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  // 处理单元格点击事件的函数
  const handleClick = (rowIndex, colIndex) => {
    handleCellClick(rowIndex * cols + colIndex); // 将二维坐标转换为一维索引并传递给父组件处理点击事件
  };

  return (
    <div className="grid">
      {cellStates.map((alive, index) => (
        <Cell
          key={index}
          alive={alive}
          onClick={() => handleClick(Math.floor(index / cols), index % cols)} // 将一维索引转换为二维坐标并传递给点击处理函数
        />
      ))}
    </div>
  );
};

export default Grid;
