// Grid.js
import React, { useState, useEffect } from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([]);

  // 初始化网格状态，随机设置单元格的生死状态
  useEffect(() => {
    setGrid(initializeGrid());
    setCellStates(initializeGrid()); // 更新cellStates为初始化的状态
  }, [rows, cols]);

  function initializeGrid() {
    const newGrid = Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => Math.random() < 0.05)
    );
    return newGrid;
  }

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <Cell
              key={colIndex}
              alive={grid[rowIndex][colIndex]} // 直接传递网格中的状态
              onClick={() => handleCellClick(rowIndex * cols + colIndex)} // 传递点击事件处理程序
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
