// Grid.js
import React, { useState, useEffect } from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([]);

  // 初始化网格状态，创建空白的网格
  useEffect(() => {
    setGrid(initializeGrid());
  }, [rows, cols]);

  // 更新网格状态，处理单元格点击事件
  const updateCell = (row, col) => {
    const newGrid = [...grid];
    newGrid[row][col] = !newGrid[row][col]; // 切换单元格状态
    setGrid(newGrid);
  };

  function initializeGrid() {
    return Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => Math.random() < 0.05)
    );
  }


  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <Cell
              key={colIndex}
              alive={cell} // 根据网格状态传递存活/死亡状态
              onClick={() => {
                updateCell(rowIndex, colIndex); // 处理单元格点击事件
                handleCellClick(rowIndex * cols + colIndex); // 调用父组件的点击处理程序
              }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
