import React, { useState, useEffect } from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([[]]);

  useEffect(() => {
    setGrid(initializeGrid());
  }, [rows, cols]);


  const updateCell = (row, col) => {
    const newGrid = [...grid];
    const cell = newGrid[row][col];
    const newCell = { alive: !cell.alive, age: cell.alive ? 0 : cell.age + 1 }; // 如果细胞存活，则年龄重置为0，否则递增年龄
    newGrid[row][col] = newCell;
    setGrid(newGrid);
  };


  function initializeGrid() {
    return Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => ({ alive: Math.random() < 0.05, age: 0 })) // 初始化细胞状态和存活时间
    );
  }

  const getColor = (age) => {
    const maxAge = 10; // 最大存活时间为10
    const startColor = [255, 0, 0]; // 开始颜色为红色
    const endColor = [0, 0, 255]; // 结束颜色为蓝色

    // 计算每个颜色通道的渐变值
    const r = startColor[0] + (endColor[0] - startColor[0]) * (age / maxAge);
    const g = startColor[1] + (endColor[1] - startColor[1]) * (age / maxAge);
    const b = startColor[2] + (endColor[2] - startColor[2]) * (age / maxAge);

    return `rgb(${r}, ${g}, ${b})`; // 返回RGB颜色字符串
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {row.map((cell, colIndex) => (
            <Cell
              key={colIndex}
              alive={cell.alive}
              onClick={() => {
                updateCell(rowIndex, colIndex);
                handleCellClick(rowIndex * cols + colIndex);
              }}
              style={{ backgroundColor: cell.alive ? getColor(cell.age) : '#FFFFFF' }} // 根据存活时间设置颜色
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
