import React, { useState } from 'react';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
    const [grid, setGrid] = useState(initializeGrid());

  // 初始化网格状态，随机设置单元格的生死状态
  function initializeGrid(rowscols) {
    const grid = Array.from({ length: rows }, () =>
    Array.from({ length: cols }, () => Math.random() > 0.05));
    return grid;
  }

  // 处理单元格点击事件，反转单元格的状态
  function handleCellClick(rowIndex, colIndex) {
    const newGrid = [...grid];
    newGrid[rowIndex][colIndex] = !newGrid[rowIndex][colIndex];
    setGrid(newGrid);
  }

  return (
    <div style={{ display: 'grid', gridTemplateColumns: `repeat(${cols}, 20px)` }}>
      {grid.map((row, rowIndex) =>
        row.map((cell, colIndex) => (
          <div
            key={`${rowIndex}-${colIndex}`}
            alive={cellStates[rowIndex * cols + colIndex]}
            onClick={() => handleCellClick(rowIndex, colIndex)}
          />
        ))
      )}
    </div>
  );
};

export default Grid;
