import React, { useState, useEffect } from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([[]]);
  const [livingCells, setLivingCells] = useState(0);

  // 初始化网格状态，创建空白的网格
  useEffect(() => {
    setGrid(initializeGrid());
  }, [rows, cols]);

  // 更新网格状态，处理单元格点击事件
  const updateCell = (row, col) => {
    const newGrid = [...grid];
    newGrid[row][col] = !newGrid[row][col]; // 切换单元格状态
    setGrid(newGrid);
    setLivingCells(countLivingCells(newGrid));
  };

  // 计算当前存活细胞数量
  const countLivingCells = (grid) => {
    let count = 0;
    grid.forEach(row => {
      row.forEach(cell => {
        if (cell) {
          count++;
        }
      });
    });
    return count;
  };

  // 初始化网格
  function initializeGrid() {
    const newGrid = Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => Math.random() < 0.05)
    );
    setLivingCells(countLivingCells(newGrid));
    return newGrid;
  }

  // 重置网格
  const handleReset = () => {
    const newGrid = initializeGrid();
    setGrid(newGrid);
  };

  // 进行单步模拟
  const handleStepSimulation = () => {
    // 实现单步模拟的逻辑
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {row.map((cell, colIndex) => (
            <Cell
              key={colIndex}
              alive={cell}
              onClick={() => {
                updateCell(rowIndex, colIndex);
                handleCellClick(rowIndex * cols + colIndex);
              }}
            />
          ))}
        </div>
      ))}
      <div className="controls">
        <button onClick={handleReset}>Reset</button>
        <button onClick={handleStepSimulation}>Next Step</button>
        <div>Living Cells: {livingCells}</div>
      </div>
    </div>
  );
};

export default Grid;
