import React, { useState } from 'react';
import './App.css';
import Grid from './Grid';

const MIN_SIZE = 3;
const MAX_SIZE = 40;

const App = () => {
  const [rowsInput, setRowsInput] = useState(20); // 用于保存输入框的值
  const [colsInput, setColsInput] = useState(20); // 用于保存输入框的值
  const [rows, setRows] = useState(20); // 用于保存当前的行数
  const [cols, setCols] = useState(20); // 用于保存当前的列数
  const [errorMessage, setErrorMessage] = useState("");

  const handleChangeRows = (e) => {
    setRowsInput(e.target.value); // 仅更新输入框的值
  };

  const handleChangeCols = (e) => {
    setColsInput(e.target.value); // 仅更新输入框的值
  };

  const handleSubmit = () => {
    const newRows = parseInt(rowsInput);
    const newCols = parseInt(colsInput);

    if (newRows >= MIN_SIZE && newRows <= MAX_SIZE && newCols >= MIN_SIZE && newCols <= MAX_SIZE) {
      setRows(newRows); // 更新当前行数
      setCols(newCols); // 更新当前列数
      setErrorMessage("");
    } else {
      setErrorMessage(`Please enter a value between ${MIN_SIZE} and ${MAX_SIZE}!`);
      return;
    }
  };

  return (
    <div className="App">
      <div className="input-fields">
        <label htmlFor="height">Height:</label>
        <input type="number" id="height" value={rowsInput} onChange={handleChangeRows} />
        <label htmlFor="width">Width:</label>
        <input type="number" id="width" value={colsInput} onChange={handleChangeCols} />
        <button onClick={handleSubmit}>Submit</button>
        {errorMessage && <div style={{ color: 'red', marginTop: '5px' }}>{errorMessage}</div>}
      </div>

      <Grid rows={rows} cols={cols} />
    </div>
  );
}

export default App;
