// Grid.js
import React, { useState, useEffect } from 'react';
import Cell from './Cell';
import { interpolateViridis } from 'd3-scale-chromatic';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([[]]);

  useEffect(() => {
    setGrid(initializeGrid());
  }, [rows, cols]);

  const updateCell = (row, col) => {
    const newGrid = [...grid];
    newGrid[row][col] = !newGrid[row][col];
    setGrid(newGrid);
  };

  function initializeGrid() {
    return Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => ({ alive: Math.random() < 0.05, age: 0 }))
    );
  }

  const getColor = (value) => {
    return interpolateViridis(value);
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {row.map((cell, colIndex) => (
            <Cell
              key={colIndex}
              alive={cell.alive}
              onClick={() => {
                updateCell(rowIndex, colIndex);
                handleCellClick(rowIndex * cols + colIndex);
              }}
              style={{ backgroundColor: cell.alive ? getColor(cell.age / 10) : '#FFFFFF' }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
