import React, { useState } from 'react';
import './App.css';
import Grid from './Grid';

const MIN_SIZE = 3;
const MAX_SIZE = 40;

const App = () => {
  const [rows, setRows] = useState(20);
  const [cols, setCols] = useState(20);
  let errorMessage = ""; // 初始化错误消息为空字符串

  const handleChangeRows = e => {
    const value = parseInt(e.target.value);
    if (value >= MIN_SIZE && value <= MAX_SIZE) {
      setRows(value);
    }
  };

  const handleChangeCols = e => {
    const value = parseInt(e.target.value);
    if (value >= MIN_SIZE && value <= MAX_SIZE) {
      setCols(value);
      errorMessage = ""; // 清除错误消息
    } else {
      errorMessage = `Please enter a value between ${MIN_SIZE} and ${MAX_SIZE}!`;
    }
  };

  return (
    <div className="App">
      <div className="input-fields">
        <label>行数：</label>
        <input type="number" value={rows} onChange={handleChangeRows} />
        <label>列数：</label>
        <input type="number" value={cols} onChange={handleChangeCols} />
      </div>

      {errorMessage && <div style={{ color: 'red', marginTop: '5px' }}>{errorMessage}</div>}
      <Grid rows={rows} cols={cols} />
    </div>
  );
};

export default App;
