import React, { useState, useEffect } from 'react';
import Cell from './Cell';

// 导入可见光谱颜色映射对象
const colorMap = {
  '0.00': 'rgb(255,0,255)',
  '0.25': 'rgb(0,0,255)',
  '0.50': 'rgb(0,255,0)',
  '0.75': 'rgb(255,255,0)',
  '1.00': 'rgb(255,0,0)'
};

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([[]]);
  const [iterations, setIterations] = useState([]);

  // 初始化网格状态，创建空白的网格和存活迭代次数数组
  useEffect(() => {
    setGrid(initializeGrid());
    setIterations(Array.from({ length: rows }, () => Array.from({ length: cols }, () => 0)));
  }, [rows, cols]);

  // 更新网格状态，处理单元格点击事件
  const updateCell = (row, col) => {
    const newGrid = [...grid];
    const newIterations = [...iterations];
    newGrid[row][col] = !newGrid[row][col]; // 切换单元格状态
    newIterations[row][col] = newGrid[row][col] ? 1 : 0; // 更新存活迭代次数
    setGrid(newGrid);
    setIterations(newIterations);
  };

  function initializeGrid() {
    return Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => Math.random() < 0.05)
    );
  }

  // 根据存活时间获取相应的颜色
  const getColorForIterations = (iterations) => {
    const keys = Object.keys(colorMap).map(parseFloat);
    const minKey = Math.min(...keys);
    const maxKey = Math.max(...keys);

    if (iterations <= minKey) {
      return colorMap[minKey];
    } else if (iterations >= maxKey) {
      return colorMap[maxKey];
    } else {
      const lowerBound = keys.filter(key => key <= iterations).pop();
      const upperBound = keys.filter(key => key >= iterations).shift();
      const ratio = (iterations - lowerBound) / (upperBound - lowerBound);

      const lowerColor = colorMap[lowerBound].match(/\d+/g).map(Number);
      const upperColor = colorMap[upperBound].match(/\d+/g).map(Number);
      const color = lowerColor.map((channel, index) =>
        Math.round(channel + ratio * (upperColor[index] - channel))
      );

      return `rgb(${color[0]}, ${color[1]}, ${color[2]})`;
    }
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {row.map((_, colIndex) => (
            <Cell
              key={colIndex}
              style={{ backgroundColor: getColorForIterations(iterations[rowIndex][colIndex]) }} // 使用存活时间获取相应的颜色
              alive={cellStates[rowIndex][colIndex]} // 根据网格状态传递存活/死亡状态
              onClick={() => {
                updateCell(rowIndex, colIndex); // 处理单元格点击事件
                handleCellClick(rowIndex * cols + colIndex); // 调用父组件的点击处理程序
              }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
