// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Grid from './Grid';

const MIN_SIZE = 3;
const MAX_SIZE = 40;

const App = () => {
  const [rowsInput, setRowsInput] = useState(20);
  const [colsInput, setColsInput] = useState(20);
  const [rows, setRows] = useState(20);
  const [cols, setCols] = useState(20);
  const [errorMessage, setErrorMessage] = useState("");
  const [cellStates, setCellStates] = useState([]);
  const [livingCellsCount, setLivingCellsCount] = useState(0); // 更新的部分

  // 更新的部分
  const handleResetGrid = () => {
    setCellStates(Array.from({ length: rows }, () => Array.from({ length: cols }, () => false)));
    setLivingCellsCount(0);
  };

  // 更新的部分
  const handleNextStep = () => {
    const newGrid = [...grid];

    // 创建一个新的数组来记录每个细胞的存活邻居数量
    const neighborsCount = Array.from({ length: rows }, () => Array.from({ length: cols }, () => 0));

    // 计算每个细胞的存活邻居数量
    for (let i = 0; i < rows; i++) {
      for (let j = 0; j < cols; j++) {
        // 检查每个细胞周围的8个邻居
        for (let di = -1; di <= 1; di++) {
          for (let dj = -1; dj <= 1; dj++) {
            if (di === 0 && dj === 0) continue; // 跳过当前细胞
            const ni = i + di;
            const nj = j + dj;
            if (ni >= 0 && ni < rows && nj >= 0 && nj < cols && newGrid[ni][nj].alive) {
              neighborsCount[i][j]++;
            }
          }
        }
      }
    }

    // 根据游戏规则更新细胞状态
    for (let i = 0; i < rows; i++) {
      for (let j = 0; j < cols; j++) {
        const aliveNeighbors = neighborsCount[i][j];
        const currentCell = newGrid[i][j];

        if (currentCell.alive) {
          // 规则1: A living cell with less than two living neighbours dies.
          // 规则3: A living cell with more than three live neighbours dies.
          if (aliveNeighbors < 2 || aliveNeighbors > 3) {
            newGrid[i][j] = { ...currentCell, alive: false };
          }
        } else {
          // 规则4: A dead cell with exactly three live neighbours becomes a live cell, as if by reproduction.
          if (aliveNeighbors === 3) {
            newGrid[i][j] = { ...currentCell, alive: true };
          }
        }
      }
    }

    setGrid(newGrid);
  };


  // 更新存活细胞数量
  useEffect(() => {
    const count = cellStates.flat().filter(cell => cell).length;
    setLivingCellsCount(count);
  }, [cellStates]);

  const handleChangeRows = (e) => {
    setRowsInput(e.target.value);
  };

  const handleChangeCols = (e) => {
    setColsInput(e.target.value);
  };

  const handleCellClick = (index) => {
    const newCellStates = [...cellStates];
    newCellStates[index] = !newCellStates[index];
    setCellStates(newCellStates);
  };

  const handleSubmit = () => {
    const newRows = parseInt(rowsInput);
    const newCols = parseInt(colsInput);

    if (newRows >= MIN_SIZE && newRows <= MAX_SIZE && newCols >= MIN_SIZE && newCols <= MAX_SIZE) {
      setRows(newRows);
      setCols(newCols);
      setCellStates(Array.from({ length: newRows }, () => Array.from({ length: newCols }, () => false)));
      setErrorMessage("");
    } else {
      setErrorMessage(`Please enter a value between ${MIN_SIZE} and ${MAX_SIZE}!`);
      return;
    }
  };

  return (
    <div className="App">
      <div className="input-fields">
        <label htmlFor="height">Height:</label>
        <input type="number" id="height" value={rowsInput} onChange={handleChangeRows} />
        <label htmlFor="width">Width:</label>
        <input type="number" id="width" value={colsInput} onChange={handleChangeCols} />
        <button onClick={handleSubmit}>Submit</button>
        {errorMessage && <div style={{ color: 'red', marginTop: '5px' }}>{errorMessage}</div>}
      </div>

      <Grid
        rows={rows}
        cols={cols}
        cellStates={cellStates}
        handleCellClick={handleCellClick}
        handleResetGrid={handleResetGrid} // 更新的部分
        handleNextStep={handleNextStep} // 更新的部分
        livingCellsCount={livingCellsCount} // 更新的部分
      />
    </div>
  );
}

export default App;
