import React, { useState } from 'react';
import './App.css';
import Grid from './Grid';

const MIN_SIZE = 3;
const MAX_SIZE = 40;

const App = () => {
  const [rows, setRows] = useState(20);
  const [cols, setCols] = useState(20);
  const [errorMessage, setErrorMessage] = useState("");

  const handleChangeRows = (e) => {
    setRows(e.target.value);
  };

  const handleChangeCols = (e) => {
    setCols(e.target.value);
  };

  const handleSubmit = () => {
    if (rows >= MIN_SIZE && rows <= MAX_SIZE && cols >= MIN_SIZE && cols <= MAX_SIZE) {
      setErrorMessage("");
    } else {
      setErrorMessage(`Please enter a value between ${MIN_SIZE} and ${MAX_SIZE}!`);
      return;
    }
  };

  return (
    <div className="App">
      <div className="input-fields">
        <label htmlFor="height">Height:</label>
        <input type="number" id="height" value={rows} onChange={handleChangeRows} />
        <label htmlFor="width">Width:</label>
        <input type="number" id="width" value={cols} onChange={handleChangeCols} />
        <button onClick={handleSubmit}>Submit</button>
        {errorMessage && <div style={{ color: 'red', marginTop: '5px' }}>{errorMessage}</div>}
      </div>

      <Grid rows={rows} cols={cols} />
    </div>
  );
}

export default App;
