import React, { useState, useEffect } from 'react';
import Cell from './Cell';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([[]]);
  const [livingCells, setLivingCells] = useState(0);

  // 初始化网格状态，创建空白的网格
  useEffect(() => {
    setGrid(initializeGrid());
  }, [rows, cols]);

  // 更新网格状态，处理单元格点击事件
  const updateCell = (row, col) => {
    const newGrid = [...grid];
    newGrid[row][col] = !newGrid[row][col]; // 切换单元格状态
    setGrid(newGrid);
    setLivingCells(countLivingCells(newGrid));
  };

  // 计算当前存活细胞数量
  const countLivingCells = (grid) => {
    let count = 0;
    grid.forEach(row => {
      row.forEach(cell => {
        if (cell) {
          count++;
        }
      });
    });
    return count;
  };

  // 初始化网格
  function initializeGrid() {
    const newGrid = Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => Math.random() < 0.05)
    );
    setLivingCells(countLivingCells(newGrid));
    return newGrid;
  }

  // 重置网格
  const handleReset = () => {
    const newGrid = initializeGrid();
    setGrid(newGrid);
  };
// 进行单步模拟
const handleStepSimulation = () => {
    const newGrid = [];

    for (let i = 0; i < rows; i++) {
      const newRow = [];

      for (let j = 0; j < cols; j++) {
        const neighbors = countAliveNeighbors(i, j);

        // 根据规则更新细胞状态
        if (grid[i][j]) {
          if (neighbors < 2 || neighbors > 3) {
            newRow.push(false); // 规则 1 和 3：少于2个邻居或多于3个邻居，细胞死亡
          } else {
            newRow.push(true); // 规则 2：2 或 3 个邻居，细胞继续存活
          }
        } else {
          if (neighbors === 3) {
            newRow.push(true); // 规则 4：3 个邻居，死细胞复活
          } else {
            newRow.push(false);
          }
        }
      }

      newGrid.push(newRow);
    }

    setGrid(newGrid);
    setLivingCells(countLivingCells(newGrid));
  };

  // 计算细胞周围的活细胞数量
  const countAliveNeighbors = (row, col) => {
    let count = 0;

    for (let i = row - 1; i <= row + 1; i++) {
      for (let j = col - 1; j <= col + 1; j++) {
        if (i !== row || j !== col) {
          // 检查边界条件以避免越界访问
          if (i >= 0 && i < rows && j >= 0 && j < cols && grid[i][j]) {
            count++;
          }
        }
      }
    }

    return count;
  };


  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {row.map((cell, colIndex) => (
            <Cell
              key={colIndex}
              alive={cell}
              onClick={() => {
                updateCell(rowIndex, colIndex);
                handleCellClick(rowIndex * cols + colIndex);
              }}
            />
          ))}
        </div>
      ))}
      <div className="controls">
        <button onClick={handleReset}>Reset</button>
        <button onClick={handleStepSimulation}>Next Step</button>
        <div>Living Cells: {livingCells}</div>
      </div>
    </div>
  );
};

export default Grid;
