import React, { useState } from 'react';
import './App.css';
import Grid from './Grid';

const MIN_SIZE = 3;
const MAX_SIZE = 40;

const App = () => {
  const [rows, setRows] = useState(20);
  const [cols, setCols] = useState(20);
  const [errorMessage, setErrorMessage] = useState(""); // 添加了 errorMessage 的状态变量

  const handleChangeRowsCols = e => {

    if (rows >= MIN_SIZE && rows <= MAX_SIZE && cols >= MIN_SIZE && cols <= MAX_SIZE) {
      setRows(rows);
      setCols(cols);
      setErrorMessage(""); 
    } else {
      setErrorMessage(`Please enter a value between ${MIN_SIZE} and ${MAX_SIZE}!`);
      // return
    }
  };


  return (
    <div className="App">
      <div className="input-fields">
        <label>行数：</label>
        <input type="number" value={rows} onChange={(e) => setRows(e.target.value)} />
        <label>列数：</label>
        <input type="number"  value={cols} onChange={(e) => setCols(e.target.value)}/> 
        <button onChange={handleChangeRowsCols} >Submit</button>
        {errorMessage && <div style={{ color: 'red', marginTop: '5px' }}>{errorMessage}</div>}

      </div>

      <Grid rows={rows} cols={cols} />
    </div>
  );
}

export default App;
