import React from 'react';

const Cell = ({ alive, cntCellDeadCum, onClick }) => {
  // 可见光谱颜色
  const visibleSpectrum = {
    '0.00': 'rgb(255,0,255)',
    '0.25': 'rgb(0,0,255)',
    '0.50': 'rgb(0,255,0)',
    '0.75': 'rgb(255,255,0)',
    '1.00': 'rgb(255,0,0)'
  };

  // 计算在可见光谱中的位置
  const position = cntCellDeadCum / 100;

  // 选择对应位置的颜色，如果超出范围，则使用默认颜色
  const cellColor = visibleSpectrum[position] || 'gray';

  // 设置单元格样式
  const cellStyle = {
    backgroundColor: alive ? 'green' : cellColor
  };

  return (
    <div
      className="cell"
      style={cellStyle}
      onClick={onClick}
    ></div>
  );
};

export default Cell;
