import React, { useState, useEffect } from 'react';

const Grid = ({ rows, cols, cellStates, handleCellClick }) => {
  const [grid, setGrid] = useState([]);

  // 初始化网格状态，随机设置单元格的生死状态
  useEffect(() => {
    setGrid(initializeGrid());
  }, [rows, cols]);

  function initializeGrid() {
    const newGrid = Array.from({ length: rows }, () =>
      Array.from({ length: cols }, () => Math.random() > 0.05)
    );
    return newGrid;
  }

  return (
    <div style={{ display: 'grid', gridTemplateColumns: `repeat(${cols}, 20px)` }}>
      {grid.map((row, rowIndex) =>
        row.map((cell, colIndex) => (
          <div
            key={`${rowIndex}-${colIndex}`}
            alive={cellStates[rowIndex * cols + colIndex]}
            onClick={() => handleCellClick(rowIndex * cols + colIndex)}
            style={{
              width: '20px',
              height: '20px',
              backgroundColor: cell ? 'black' : 'white',
              border: '1px solid gray'
            }}
          />
        ))
      )}
    </div>
  );
};

export default Grid;
